Corpora.Source:
$Source = New-Item -ItemType Directory "$TempDir/jquery_source";
 git clone https://github.com/jquery/jquery.git $Source 2>$null;
$_ = Remove-Item "$($Source.FullName)/.git" -Recurse -Force;
return $Source;

SubCorpora.Source:
$assem=[Reflection.Assembly]::LoadWithPartialName("System.Windows.Forms");
$Corpus = New-Item -ItemType Directory "$TempDir/BMPCorpus";
foreach($tiff in Get-ChildItem -Recurse -Filter *.tiff ./ImageCorpus) {
	$img = New-Object System.Drawing.Bitmap $tiff.FullName;
	$img.Save("$($Corpus.FullName)/$($tiff.Name).bmp", "BMP");
};
Get-Item -Path $Corpus

